css:
@-moz-document domain("saintcon.org") {
body[style*="pqdICWJ"] > header > .container > h1,
body[style*="pqdICWJ"] > header > .container > p {
    color: #fff !important;
}

@media only screen and (-webkit-max-device-pixel-ratio: 1) {
    body, p, h4, h4.news, section a {
		font-weight: 400;
	}
    h1, h2, h3 {
		font-weight: 300;
	}
    .navbar-inverse .navbar-nav>li>a {
		font-weight: 400;
    }
}
}
