css:
@-moz-document domain("paulgraham.com") {
    /* 
    Name: Dark paulgraham.com
    Author: PLNech 
    Based on https://userstyles.org/styles/123343/paulgraham-com
    */
    body {
        background-image: none;
        background-color: #151515 !important;
        color: #e5e5e5 !important;  
    }
    table[width] { /* Main page layout */
        max-width: 55em;
        width: 90% !important;
    }
    table[width] > tbody > tr > td { /* Article layout */
       width: 100% !important;
       text-align: justify;
       line-height: 1.8em;
    }
    table[width] font { /* Text nodes */
        font-size: 16px;
    }
    a {
       color: coral;
    }
    table {
       margin: auto;
       border-right: transparent solid 3em;
    }
    img {
        background-color: #EEE;
    }
    table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(3) > a:nth-child(1) > img:nth-child(1) { /* Main logo */
       filter: invert(0.92);
    }

    body > table > tbody > tr > td > table:nth-child(4) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > font:nth-child(4) > table:nth-child(382),
    body > table > tbody > tr > td > table:nth-child(6) { /* Footer links */
        border: 0 none;
        max-width: 10em !important;
        background-color: #222 !important;
        white-space: pre;
    }
    img[height="14"]{ /* Footer bullet points */
        filter: invert(0.92);
    }
    body table tbody tr td table tbody tr td font table tbody tr td { /* Header & Footer banners */
        background-color: #222 !important;
    }
    body > table > tbody > tr > td > table > tbody > tr > td > font:nth-child(4) > table:nth-child(384) > tbody:nth-child(1) > tr:nth-child(1) > td { /* Footer bar*/
        background-color: #151515 !important;
    }
}
