css:
@-moz-document domain("www.wuxiaworld.com") {
/* Readility specific styles */
html {background-color: #333 !important}
html>body{
    margin:0 auto !important;
}

html>body,.section-content,.panel-default,
body p, body span, body ul li{
background-image:none !important;
background-color:#333 !important;
    color:#eee !important;
    font-size:20px !important;
}
.list-chapters a span {
font-size:16px !important;
}

/* full width */
.space-0>.row,.space-0>.row>.col-lg-9,.container {
display:block !important;
width:100% !important;
overflow:hidden !important;
padding:0 !important;
margin:0 auto !important;
}
.header,.space-0>.row>.col-lg-3,.font-resize{
display:none !important;
}
.panel-default{
     width:60% !important;
    margin:0 auto !important;
}
#accordion .panel-default{
width: auto !important;
}
#accordion .panel-body{
background-color:inherit !important;
border-bottom:#999 !important;
}
.panel-title a{
color:#337ab7 !important;
}
a:visited{ color: #20A2FC !important; }
h1,h2,h3,h4,h5,h6 {
font-size:1.2em !important;
    background-color:#333 !important;
    color:#eee !important;
    margin:1.2em !important;
}
ul h4{ margin: 0 !important}
p {
  font-family: -apple-system,BlinkMacSystemFont,"Segoe UI",Roboto,Oxygen-Sans,Ubuntu,Cantarell,"Helvetica Neue",sans-serif !important;
 line-height:1.8em !important;
}

/* tablet */
@media (max-width:961px){
.panel-default{
     width:96% !important;
    }
html>body,.section-content,.panel-default,
body p, body span, body ul li{
font-size:18px !important;
}
p {
line-height: 1.5em !important;
}
}
}
