css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("getpocket.com") {

  
  .text_body h1:lang(fa), .text_body h2:lang(fa), .text_body h3:lang(fa), .text_body h4:lang(fa), .text_body h5:lang(fa), .text_body h6:lang(fa){
    direction: rtl !important;
    text-align: right !important;
    font-family: iransans, tahoma;
  }
  .text_body p:lang(fa), div:lang(fa) {
    direction: rtl !important;
    text-align: right !important;
    font-family: iransans, tahoma;
    font-size: 14px;
  }
  .text_body li:lang(fa){
    direction: rtl !important;
    text-align: right !important;
    font-family: iransans, tahoma;
    font-size: 14px;
  }

}
