css:
@-moz-document domain("steamcharts.com") {
  * {transition: 0.3s ease; -webkit-transition: 0.3s ease; -moz-transition: 0.3s ease; font-family: 'Roboto', 'Arial' !important; font-weight: 300 !important;}
  body {background: #111;}
  #footer, #closing {display: none;}
  .twitter-timeline {display: none !important;}
  .content {background: #151515; margin-left: -125px; margin-bottom: 10px; border: 1px solid #222; width: 960px;}
  .ad720 {display: none !important;}
  input[type="text"], input[type="password"] {border-radius: 10px;}
  #content-wrapper {padding-bottom: 0 !important;}
  #overview-chart {width: 960px !important; margin-left: 125px;}
  .chart {margin: none;}
  .content:first-child {background: #444 !important;}
  #render_frame_1 {display: none !important;}
  .app-stat {margin-left: 80px;}
}
