css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https:\/\/bitbucket\.org\/[^\/]*\/[^\/]*\/issues($|[?\/].*)") {
  /* { display: table-cell; } to show, { display: none; } to hide */
  
  /* Title */
  td.text, th.text { display: table-cell; }
  
  /* Type and Priority */
  td.icon-col, th.icon-col { display: table-cell; }
  
  /* Status */
  td.state, th.state { display: table-cell; }
  
  /* Votes */
  td.votes, th.votes { display: none; }
  
  /* Assignee */
  td.user, th.user { display: none; }
  
  /* Milestone */
  td.milestone, th.milestone { display: none; }
  
  /* Version */
  td.version, th.version { display: table-cell; }
  
  /* Created and Updated */
  td.date, th.date { display: table-cell; }
}
