css:
@-moz-document domain("smashwords.com") {
body {
    background: #222;
    color: #ddd;
}
a, a:link, a:visited, .footer a {
    color: #4593e3;
}
a:hover {
    color: #67acf3
}
input {
    background: #666;
    color: black;
    border: 2px inset black;
}
input.form-control {
    background: #444 !important;
    color: #ddd !important;
    border: 2px inset black;
}
.label {
    color: #ddd;
}



/* the blue headers here and there */
.left-nav-box>h1:first-child, #header #navMenu {
    background: #1d4167;
}

/* synopsis box in search results */
.well {
    background: #2c2c2c;
    color: #ccc;
    border: 1px solid black !important;
}

/* metadata in search results */
.subnote {
    color: #999;
}

/* category box in search results */
.left-nav-box {
    background: #333;
}
.left-nav-box a {
    color: #ddd !important;
}

/* top left logo */
#smashlogo img {
    filter: invert(100%) contrast(80%) brightness(140%) saturate(90%) hue-rotate(180deg);
    -webkit-filter: invert(100%) contrast(80%) brightness(140%) saturate(90%) hue-rotate(180deg);
}

/* ugly tables */
td, .list-group-item, .userProvidedContent {
    border: 1px solid #111 !important;
    background: #2c2c2c !important;
}
th, .breadcrumb {
    border: 1px solid black !important;
    background: #141414 !important;
}

/* fancy boxes (infobox, social media stuff, etc) */
.panel {
    background: #2c2c2c;
}
.panel-default > .panel-heading {
    background: #191919;
    border-color: black;
    color: #ccc;
}
.panel-default {
    border-color: black;
}

/* important notice box */
.alert {
    background-color: #68b;
    border-color: #111;
    color: #111;
}
.alert > strong > span, .alert > a {
    color: black !important;
}

/* "also in series" slider boxes */
.bx-wrapper, .bx-wrapper .bx-viewport, .slider-slide {
    background: #191919;
    box-shadow: none;
}
.bx-wrapper {
    border: 2px solid black;
    padding: 4px 2px;
    padding-left: 8px;
}
.slider-slide > a > img {
    border: 1px solid black;
}

/* dropdown user menu */
.user-menu a {
    color: #eee;
}
.user-menu {
    background: #333;
    border: 2px solid #111;
    box-shadow: 5px 5px 10px 2px #111;
}

/* ======= BUTTONS ======================== */

/* pagination */
.pagination > li > a {
    background: linear-gradient(to bottom, #457, #112);
    color: #ddd;
    font-weight: bold;
    border-color: black;
}
.pagination > .active > a, .pagination > li > a:hover {
    background: linear-gradient(to bottom, #9bd, #246);
    border-color: black !important;
}

/* misc buttons */
.btn a {
    color: #ddd !important;
}
.btn-default {
    background: linear-gradient(to bottom, #457, #112);
    color: #ddd !important;
    border-color: black;
}
.btn-default:hover, .btn-default.active {
    background: linear-gradient(to bottom, #9bd, #246);
    color: #fff;
    border-color: black;
}

/* yellow buttons */
.btn-primary {
    background: linear-gradient(to bottom, #b80, #640);
    color: black !important;
    border-color: black;
}
.btn-primary:hover {
    background: linear-gradient(to bottom, #fc0, #b90);
    color: black;
    border-color: black;
}

/* green buttons */
.btn-booklist {
    background: linear-gradient(to bottom, #7a4, #261);
    color: black !important;
    border-color: black;
}
.btn-booklist:hover {
    background: linear-gradient(to bottom, #bf5, #582);
    color: black;
    border-color: black;
}
.btn.nolinks {
    color: black;
}

/* adult content button */
.btn-info, .btn-info:active, .btn-info:visited {
    background: linear-gradient(to bottom, #246, #024);
    border-color: black;
}
.btn-info:hover {
    background: linear-gradient(to bottom, #48c, #246);
    border-color: black;
}

li.adult-control:hover  {
    background: none !important;
}



}

@-moz-document regexp("https?://www\\.smashwords\\.com/extreader/read/.+") {
/* the online reader */

/* scoot the email sample link up a bit to look better */
#pageContent > div:first-child {
    margin-bottom: 10px !important;
}

/* fix the main layout thingy */
#pageCenterContent {
    background: #333 !important;
    border: 1px solid #111;
    border-radius: 5px;
    box-shadow: 0 0 10px 2px #191919;
}
#bookContent p {
    color: #eee;
}
#pageBody a, .footer a {
    color: #4593e3 !important;
}

/* the page buttons at the bottom of the reader */
#simpleControls {
    background: #333 !important;
    border-radius: 5px;
    padding-bottom: 5px;
}
#simpleControls img {
    filter: invert(100%) contrast(80%) brightness(200%) saturate(170%) hue-rotate(175deg);
    -webkit-filter: invert(100%) contrast(80%) brightness(200%) saturate(170%) hue-rotate(175deg);
}


}
