css:
@-moz-document regexp("http((s)?)\\:\\/\\/((www\\.)?)watch2gether\\.com\\/rooms\\/(.*)") {
/* Darken */
.ui.page.grid, body.pushable>.pusher {
	background-color: rgb(27, 27, 25);
}

.main-content, #player-chat, #client-container, #userbar, #chat-input, #search-container, .ui.page.grid .fixed {
	background-color: #32322C;
}

div.card {
    height: 90px !important;
    width: 40px !important;
}

div.videocard {
    height: 200px !important;
    width: 150px !important;
}

#player-chat {
	position: relative !important;
}
.ui.tabular.menu .item {
	color: white;
}

#player-video-info {
	color: white;
	background-color: #32322C;
}

.active.item {
	color: black;
}

/* Bigger Player */
#player {
	width: 163% !important;
}

.ui.grid>[class*="six wide"].column {
	width: 100% !important;
}

#userbar {
	height: 100px !important;
}

.fixed {
	position: static !important;
}

/* Adjust footer */
.footer {
	min-height: 0px;
	padding: 0px;
}

/* Fix container */
#client-container {
	border: 1px solid #ddd;
}
}
