css:
@-moz-document domain(colorhunt.co) {
body {
    background-color: #192633 !important;
}
.home {
  background-color: #192633 !important;
}
.header {
  background-color: #322864 !important;
}
.filter a:hover {
    color: #A6FFF2 !important;
}
.filter a {
    color: #fff !important;
}
.header.shadow > a::before {
    position: absolute !important;
    content: "" !important;
    width: 60px !important;
    height: 60px !important;
    border-radius: 100% !important;
    background-color: #fff !important;
    top: 10px !important;
    left: calc(50% - 30px) !important;
}
.logo {
  top: 22px !important;
}
.new.tran {
  color: #d1fff8 !important;
}
span.icon-menu.tran {
    color: #fff;
}
span.icon-menu.tran:hover {
    color: #d1fff8 !important;
}
.item {
  background-color: #303b47 !important;
}
.date {
  color: #fff !important;
}
}
