css:
@-moz-document domain(answers.unity3d.com) {

  /* remove left padding for inline markdown code `some inline things` */
  .mainContent code {
    padding-left: 4px;
  }

  /* remove line numbers + white left padding */
  .mainContent ol {
    list-style-type: none;
    margin-left: 0px;
    padding-left: 0px;
  }

  .mainContent pre.prettyprint {
    padding-left: 0px;
  }
}
