css:
@-moz-document domain("reddit.com") {
/* List */

html.SHINE body > div.content {
    padding: 0!important;
}
html.SHINE.shine-list body > div.content div#siteTable.linklisting {
    width: 100%!important;
    margin: 20px auto 0px!important;
}
html.SHINE.shine-list.shine-list-classic body > div.content div#siteTable.linklisting {
    width: 40%!important;
    margin-left: 0px!important;
}
html.SHINE.shine-list body > .content #siteTable .thing.link > .child {
    display: none!important;
}
html.SHINE.shine-list body > div.content div#siteTable.linklisting > .thing {
    margin: 0px!important;
    padding: 5px 10px 10px 130px!important;
}
html.SHINE.shine-list.shine-list-classic.show-shortcuts .shine-expand {
    top: 70px!important;
    height: calc(100% - 70px)!important;
}
html.SHINE.shine-list.shine-list-classic .shine-expand {
    position: fixed!important;
    left: calc(40% + 10px)!important;
    width: calc(60% - 20px)!important;
}
html.SHINE .shine-nav {
    position: fixed!important;
    right: calc(60% - 40px)!important;
    bottom: -10px!important;
}
}
