css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document url("https://www.destiny.gg/embed/chat") {
  * {
    font-family: monospace;
    font-size: 11px;
  }
  
 
  .chat-theme-dark .chat-lines time {
    color: #928374;
  }
  
  
  body {
    color:#ebdbb2;
    
  }
  .chat-theme-dark #chat-input-control input {
    color:#ebdbb2;

  }
  .chat-lines a.user {

  }
  .chat-lines {


  }
  .chat-input-control input, .chat-lines {


  }
  #destinychat {
    background-color: #282828;
  }
  .chat-theme-dark #chat-input-control input, .chat-input-wrap, .chat-input > div {
    background-color: #282828;

  }
  .chat #chat-lines > .own-msg, .chat #chat-lines > .broadcast-msg{
    background-color: #3c3836;
    color:#ebdbb2;
  }
  #chat-input-control {
    color: #ebdbb2;
  }
  #chat-input > div, #chat-input-control-wrap{
    background-color:#282828;

 
  }
  #chat-input > div{
    border-top: 1px #928374 dashed;
    
  }
  .chat-theme-dark .chat-input-control {
    background:#282828;
  }
  
  .chat-input-control input, .chat-input-wrap, .chat-input > div {
    border-top: 1px #928374 dashed;
  }
  
  .chat #chat-lines > .highlight {
    background-color:#665c54;
  }
  
  #chat-scroll-notify {
    
    background: rgba(29,32,33, 0.85)
  }
  #chat-lines a.externallink {
    color: #ebdbb2;
  }
  #chat-lines a.externallink:hover {
    color: #ebdbb2 !important;
    border:none;
    text-decoration: underline;
  }
  #chat-lines a.externallink:visited {
    color: #ebdbb2;
  }
}
