css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.gizmodo.jp") {
#head_main > *,
[id^="ad_"],
[id^="ads_"],
.e_com_tra+p
{display:none !important;}

#head_main > h1
{display:block !important;}

#head_main{height:120px !important;}
#body{clear:both !important;}

}

@-moz-document domain("www.lifehacker.jp") {
#head_main { background: none !important; height: auto !important;}
#head_main p, #head_main div, #head_main ul, #head_main h3 { display: none !important;}
#head_main h1 { display: block !important; position: static !important;}

[id^="google_ads_frame"],
[id^="ads_"],
[id^="ad_"],
.entry_body .center+*,
#description,
#subscribe,
#amazon
{display:none !important;}

}
