css:
@-moz-document domain("github.com") {
.dashboard .news .alert {
    background-color: #f5f5f5;
    border: 1px solid #d8d8d8;
    border-radius: 3px 3px 3px 3px;
    margin-bottom: 10px;
}
.dashboard .news .alert:hover {
    background-color: #e6e6e6;
}
.dashboard .news .alert:first-child .body {
    padding-top: 1em;
}
.dashboard .news .alert:first-child .dashboard-event-icon {
    top: 18px;
}
.dashboard .news .alert .simple .title {
    font-size: 15px;
}
.dashboard .news .alert .simple .title {
    font-weight: bold;
}
.dashboard .news .alert .octicon-star {
    color: #FF851B;
}
.dashboard .news .alert .octicon-issue-opened {
    color: #FF4136;
}
.dashboard .news .alert .octicon-git-pull-request {
    color: #3D9970;
}
.dashboard .news .alert .octicon-repo {
    color: #85144b;
}
.dashboard .news .alert .octicon-git-branch {
    color: #2ECC40;
}
.dashboard .news .alert .octicon-person {
    color: #001f3f;
}
.dashboard .news .alert .simple .time {
    float: right;
    padding-right: 10px;
}
}
