css:
.navbar-nav>.user-menu>.dropdown-menu>.user-body, button.btn.btn-main.dropdown-toggle, .skin-blue .wrapper, .skin-blue .main-sidebar, .skin-blue .left-side, div.box, div.box-body, div.col-md-4, li.header.text-center, aside.left-side.sidebar-offcanvas, div.panel-footer {
    background-color: #292929;
}

.navbar-nav>.user-menu>.dropdown-menu>.user-footer .btn-default, div.box-body h1, thead tr th, span.title, div.box-body strong, .navbar-nav>.user-menu>.dropdown-menu>.user-body a, h4.fixed, aside.right-side {
    color: #999 !important;
}

li.item, textarea.form-control, button, input, select, textarea, tbody tr td, div.tab-content, ul.nav.nav-tabs, li.active a, .modal-header, div.modal-body, div.modal-footer, .breadcrumb, .dropdown-header, .pull-right>.dropdown-menu, div.box-body, .panel-default>.panel-heading, a.btn.btn-app, div.box-header.with-border, .skin-blue .sidebar-menu>li.header, span.input-group-addon, li.list-group-item, div.box-body ul li, .navbar-nav>.user-menu>.dropdown-menu>.user-footer, a.btn.btn-default, input.form-control, a.btn.btn-default.pull-right, div.panel-body, div.panel-heading, section.content-header h1, section.content, section.content-header, aside.right-side {
    background-color: #292929 !important;
    color: #999 !important;
}
::-webkit-scrollbar {
    width: 12px;
    background-color: #999;
}
 
::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3); 
    border-radius: 10px;
}
 
::-webkit-scrollbar-thumb {
    border-radius: 10px;
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.5); 
}

