css:
@-moz-document url-prefix("http://www.smogon.com/dex/") {
/* Created by mahoiku @ https://mahoiku.moe */
/* im new to this ok dont kill me */
body {
    background-color: #0b1933!important;
}
a:link {
    color:#3175f3;
}

a:hover {
	background:#6262af!important;
}
.Type {
  color: white!important;   
}
* {
    color: white;
}
input {
    color: black!important;
}
textarea {
    background:#233250;
}
.PokemonAltInfo-sprite,.ExportButton,.MoveRow,.PokemonAltRow,.AbilityRow,.ItemRow,.TagRow {
     background-color: #233250!important;
}
.DexContent,.DexBody,.container,.DexSearch-results {
     background-color: #0b1933!important;
}

.DexFocused {
    background-color: #3178fc!important;
}
.hlchars {
    background-color:#3178fc;
}
.PokemonPage-StrategySelector,.BlockMovesetInfo,.BlockMoveInfo,.DexTable,.DexSearch-input{
    background: #233250!important;
}

.DexTable{
    border:1px solid #d2d2ec;
    border-radius:8px;
    padding: 5px;
    box-shadow: 1px 1px 1px #ccc;
}
}
