css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("deviantart.com") {

div#top div.ch-ctrl {margin:0 -20px!important;}
div#top div#top-collect {position:fixed!important;}
div#top div#top-collect i.bl.c,
div#top div#top-collect i.br.c {display:none!important;}
span.glink.gspecial.friendsmenu,
span.glink.gspecial.collectmenu {display:none!important;}
span.logout a.blend {background:none!important;}
div#sitemap-login-menu {position:fixed!important;width:180px!important;background-image:none!important;}
div.popup2-shadow {background-image:none!important;}
img.popup2-shadow {display:none!important;}

div#top div.ch-ctrl {background-color:purple!important;}
div#top div.text.loggedin span,
div#top div.ch-ctrl a.blend {color:#c0c!important;}
div#top div.ch-ctrl div.text.loggedin {color:pink!important;}
div#top div.ch-ctrl span.glink.loginmenu span a {background-image:url(http://i43.tinypic.com/np2crs.png)!important;background-position:0 6px!important;}

}

@-moz-document url("http://*.deviantart.com/art/*") {

div#sitemap-login-menu {top:0px!important;}

}
