css:
@-moz-document url-prefix("http://www.bilibili.com/video") {
.arrow_r {
    right: 0px !important;
}

.cmt {
    text-shadow: rgb(0, 0, 0) 0px 0px 6px !important;
}

.bilibili-player-video-wrap {
    height: 498px;
}

.bilibili-danmaku {
    text-shadow: rgb(0, 0, 0) 0px 0px 6px !important;
    font-weight: normal !important;
}

.bilibili-player-video-message,
.bilibili-player-video-state,
.bilibili-player-promote-wrap,
.bilibili-player-ending-panel,
.bilibili-player-electric-panel,
.bilibili-player-video-hint {
    height: 0px !important;
    display: none !important;
}

.bilibili-player-info {
    height: 52px !important;
}

.bilibili-player-watching {
    padding-top: 8px !important;
}

.bilibili-player-video-inputbar {
    background: white !important;
}

.bilibili-player-video-sendbar {
    background-color: #fff;
    height: 28px;
}

.bilibili-player-video-sendbar>div.bilibili-player-video-btn,
.bilibili-player-video-sendbar>div.bilibili-player-video-inputbar {
    height: 28px;
    line-height: 28px;
}

.bilibili-player-video-sendbar .bilibili-player-video-inputbar .bilibili-player-video-btn-send {
    top: 2px;
    right: 5px;
    height: 20px;
    line-height: 20px;
    padding: 0 10px 0 10px;
    width: 35px;
}

.bilibili-player-video-sendbar .bilibili-player-video-inputbar .bilibili-player-video-danmaku-input {
    height: 28px;
}

.bilibili-player-mode-selection-container,
.bilibili-player-color-picker-container {
    bottom: 28px;
}

.bilibili-player .mCSB_scrollTools .mCSB_draggerRail {
    width: 10px;
}

.bilibili-player .mCSB_scrollTools .mCSB_dragger .mCSB_dragger_bar {
    width: 10px;
}

.mCS-autoHide>.mCustomScrollBox>.mCSB_scrollTools,
.mCS-autoHide>.mCustomScrollBox~.mCSB_scrollTools {
    margin-right: -2px;
}

.mCSB_scrollTools .mCSB_draggerContainer {
    opacity: 0.8;
}
}
