css:
@-moz-document domain("acolyer.org") {
body {
	margin: 0 20px;
	background: #f3f3f3;
}
body,
p {
	font-family: 'Merriweather', Georgia, 'Times New Roman', Times, serif;
	-moz-osx-font-smoothing: grayscale;
	-webkit-font-smoothing: antialiased !important;
	-moz-font-smoothing: antialiased !important;
	text-rendering: optimizelegibility !important;
	text-align: justify;
	text-justify: inter-word;
	word-spacing: .15em;
	font-size: 85%;
	font-weight: 500;
	line-height: 1.3;
}
#wrapper {
	margin: 0;
	padding: 0;
	width: 100%;
}
#content {
	float: left;
	width: 80%;
	padding: 20px 0;
}
#sidebar {
	float: right;
	width: 20%;
	padding: 0;
}
.entry {
	max-width: 100%;
}
.entry {
	color: #222;
	background: #fcfcfc;
}
.thin-sidebar {
	width: 100%;
	padding: 0 10px;
}
iframe#twitter-widget-0 {
	width: 100% !important;
	padding: 0 10px;
}
blockquote {
	background: #f2f2f2;
	border-left: 10px solid #888;
	margin: 1em 10px;
	padding: 0.9em 10px;
	quotes: "\201C""\201D""\2018""\2019";
}
blockquote:before {
	color: #888;
	content: open-quote;
	font-size: 4em;
	line-height: 0.1em;
	margin-right: 0.25em;
	vertical-align: -0.4em;
}
blockquote p {
	display: inline;
}
}
