css:
@-moz-document domain("wf.poedb.tw") {
/* main text & bg 主頁面 文字、背景 */
body {
	color: #DDD;
	background-color: #333; }

/* container text & bg 文字、背景 */
.container {
	color: #DDD;
	background-color: #333; }

/* link 連結顏色 */
a {	color: #69E; }

/* alarm text & bg 警報頁面背景 */
.badge {
	color: #EEE;
	background-color: #880; }

/* alarm table border 警報頁面 表格框線 */
.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th {
	border-top: 1px solid #888; }

/* hr 分隔線 */
hr { border-top: 1px solid #AAA; }

/* filter tags 過濾標籤背景 */
.tags span { background: #800; }

/* navbar text 文字 */
.navbar-default .navbar-nav>li>a { color: #DDD; }
.navbar-default .navbar-brand { color: #EEE; }

/* table bg & border 表格背景、邊框 */
table.table-bordered>tbody>tr>td, .table-bordered>tbody>tr>th, .table-bordered>tfoot>tr>td, .table-bordered>tfoot>tr>th, .table-bordered>thead>tr>td, .table-bordered>thead>tr>th {
	background-color: #333;
	border: 1px solid #888 !important; }

/* Detals button */
button, input, select, textarea { background-color: #800; }

/* Search Bar */
.form-control {
    color: #ddd;
    background-color: #333;
    border: 1px solid #777; }

.btn-default {
    background-image: -webkit-linear-gradient(top,#aaa 0,#999 100%);
    background-image: -o-linear-gradient(top,#aaa 0,#999 100%);
    background-image: -webkit-gradient(linear,left top,left bottom,from(#aaa),to(#999));
    background-image: linear-gradient(to bottom,#aaa 0,#999 100%);
    border-color: #777; }
	
.btn-default:focus, .btn-default:hover {
    background-color: #999; }

}
