css:
@-moz-document domain("seasonvar.ru") {
html {
	display: none;
}
body {
	display: flex;
	flex-flow: row;
	flex-wrap: wrap;
	background: #fff;
	padding-top: 50px !important;
}
#mycontent {
	flex: 10;
}
#mycontent #grid {
	display: flex;
	flex-flow: row;
	flex-wrap: wrap;
}
#mycontent .item {
	flex: 1 0 450px;
	margin: 12px;
	text-decoration: none;
	border: 1px solid #ddd;
	padding-bottom: 10px;
	background: #EFF3F2;
	border-radius: 5px 5px 5px 5px;
	-moz-border-radius: 5px 5px 5px 5px;
	-webkit-border-radius: 5px 5px 5px 5px;
}
#mycontent .item {
	color: #000;
	font-size: 14px;
	padding: 10px;
}
#sidebar {
	flex: 1 0 250px;
	text-align: left;
	padding: 20px 40px;
}
#sidebar .betterT {
	color: #000;
}
#sidebar .tnt-2link {
	display: none;
}
#sidebar .alf-letter {
	position: relative;
	cursor: pointer;
}
#sidebar #ifilter label {
	color: #000;
}
#header {
	display: block;
	width: 100%;
	height: 40px;
	background: #333;
	text-align: center;
	padding-top: 15px;
	position: fixed;
	top: 0px;
	z-index: 6;
}
#header form {
	display: inline-block;
}
#header form input.form-textnew {
	position: relative;
	top: auto;
	display: block;
	float: left;
	width: 300px;
	font-size: 14px;
}
#header form input.form-searchnew {
	left: auto;
	position: relative;
	top: auto;
	display: block;
	height: 25px;
}
.searchResult {
	text-align: left;
	font-size: 14px;
}
.item .serial-r p {
	color: #000;
}
.item .serial-r div {
	color: #000;
}
body .sidebar.lside {
	left: 0px;
}
.pgs-sinfo,
.pgs-seaslist,
#player_wrap {
	max-width: 800px;
	margin: 0 auto;
}
#player_wrap {
	padding-bottom: 100px;
}
.pgs-sinfo {
	padding-top: 20px;
}
}
