css:
@-moz-document regexp("http://*.*.*.*/current") {
html {
    height: 100%
}
body {
	font-family: "Segoe UI","Verdana";
}
.smallerText {    
    margin: auto;
}
table, th, td {
    border: 0px;
    border-collapse: collapse;
    text-align: center;
    padding: 3pt 5pt;
}
tr:nth-child(even) {
    background-color: #111111;
}
tr:hover {
    background-color: #f5f5f5;
    color: #222222;
}
th {
    background-color: #000000;
    color: white;
    padding: 0pt 5pt;
}
}
