css:
@-moz-document domain("lowendtalk.com"), domain("www.lowendtalk.com") {
.ControlOptions,
.Options,
.MItem.LastCommentBy,
.Tag,
.MItem.Category,
.IndexPhoto.PhotoWrap {
    display: none;
}

.Discussion.ItemContent {
    padding-left: 0;
}

.DataList .Title {
    display: inline-block;
    float: left;
    padding-right: 12px;
}

.DataList.Discussions .Item {
	padding: 4px;
    border-bottom: 1px solid #f1eff3;
}

#Panel {
    position: fixed;
    overflow: auto;
    right: 20px;
    height: 100%;
    top: 0;
}
.Column.ContentColumn {
    width: 100%;
}

#Frame {
    margin: 0;
    width: calc(100% - 300px);
}
#Body {
    width: auto;
    max-width: 90%;
}
#Head {
    padding: 0 5%;
}
#Body > .Row {
    width: auto;
}
}
