css:
@-moz-document domain('vk.com'), url-prefix('http://vk.com/') {

.w-banner, #banner-placeholder, .games, .games_with-categories, .layout__centercol, .layout__promotoolbar, .js-social-icq, .social__title, .social, .social_ok, #leftcol-banners, .b-layout__col__group.b-layout__col__group_leftcol-banners{
	display:none !important;
}
.b-rb * :not(.datalist__head__item){
	display:none !important;
	visibility:hidden !important;
}
.b-rb, .b-datalist__head__item{
	display:none !important;
	visibility:hidden !important;
}
.b-informer, .b-informer *{
	display:block !important;
	visibility:visible !important;
}
.b-informer{
	margin-top:-35px !important;
}
#leftcol-banners *, #b-prefoot{
	display:none !important;
}

}
