css:
@-moz-document domain("breitbart.com") {
/*NW*/
div.post-content > div.post-body div.post-message p,
div#layout section#conversation div#posts li.post div.post-content div.post-body div.post-body-inner div.post-message-container div.publisher-anchor-color div.post-message p {
   color: pink !important;
}
body, 
div#ContentW, 
div#ContainerW {
    background-color: #333 !important;
    background-image: none !important
}
/* main container */
h3 {
    font-family: liberation sans narrow, georgia
}

div#ContainerW,
div#disqus_thread,
div#SideW {
   background-color: gray !important
}
li.post,
div.post-body div.post-body-inner div.post-message-container div.publisher-anchor-color div.post-message p,
div.post-content {
   color: pink !important
}
/* main article, right sidebar */

div.container,
div.entry-content,
div.SWW,
div.ndn-videos.SWW h2,
ul#disqus-popularUL,
ul#HPSWUL {
   background-color: lightgray !important;
   font-family: "droid sans", verdana;
   color: black;
   padding: 5px
}
h2.title,
a.thumbnail-url {
   color: blue !important
}
div#comments,
div#MainW,
div#disqus_thread {
   background-color: lightgray !important;
   border-radius: 15px
}
}
