css:
@-moz-document domain('vk.com') {
#side_bar_inner {
  position: fixed!important;
  margin-top: 0px!important;
  top: 42px!important;
}
 @media (min-width: 1281px) {
  #page_layout,#page_header{
    padding-right: 150px;
  }
  #top_profile_menu{
    margin-right: 90px;
  }
 }
}
@-moz-document url-prefix('https://vk.com/feed') {
  @media (min-width: 1600px) {
      #page_layout,#page_header{
                                              width:75%!important;
      }
      #page_body {
                                              width:87%!important;
      }
  }
  @media (max-width: 1600px) and (min-width: 1275px) {
      #page_layout,#page_header{
                                              width:85%!important;
      }
      #page_body {
                                              width:85%!important;
      }
    #stl_bg {width: 70px;}
    #stl_text {font-size: 0px!important;}
  }

  .page_media_link_img {
      width: inherit!important;
  }
  .page_media_thumbed_link {
      width: 508px!important;
  }
}
