css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mirkoczat.pl") {
  
  .chat > div:last-child > .message:nth-child(odd){
    background-color:#252525;
  }
  
  .chat > div:last-child > .message > a[title] {
    position: relative;
    top:5px;
    display:inline-block;
    font-size:18px;
    width:100px;
    max-width:100px;
    overflow-x:hidden;
    text-align:right;
    margin-right:20px;
    margin-left:20px;
  }
  
  .chat > div:last-child > .message > span{
    position: absolute;
    margin-top:10px;
  }
}
