css:
@-moz-document domain("www.bloxcity.com") {
body {
    background-color: #333;
}
.nav-wrapper, .white {
    background-color: #444!important;
    box-shadow: none!important;
    color: white!important;
}
nav[style="box-shadow:none;border-bottom: 1px solid #e8e8e8;"] {
    box-shadow: none!important;
    border-bottom: none!important;
}
.site-footer {
    background-color: #222!important;
}
.bc-content {
    box-shadow: none!important;
}
.side-menu-link:hover {
    background: #202020!important;
}
.side-menu-link, .side-menu-title {
    color: white!important;
}
nav a, nav ul a {
    color: white!important;
}
.dropdown-content {
    top: inherit!important;
    background-color: #333!important;
}
nav ul a:hover {
    background-color: rgb(36, 36, 36)!important;
}
.dropdown-content li:hover, .dropdown-content li.active, .dropdown-content li.selected {
    background-color: rgb(36, 36, 36)!important;
}
a[style*="color:#323a45;font-weight:600;"] {
    color: white!important;
}
div[style*="padding:10px 0;font-size:14px;color: #666;"] {
    color: white!important;
}
div[style*="padding:10px 0;font-size:14px;color:#666;"] {
    color: white!important;
}
.select-dropdown li.disabled, .select-dropdown li.disabled>span, .select-dropdown li.optgroup {
    color: rgb(255, 255, 255);
    background-color: transparent;
}
.dropdown-content li>a, .dropdown-content li>span {
    font-size: 16px;
    color: rgb(255, 255, 255);
    display: block;
    line-height: 22px;
    padding: 14px 16px;
}
.container > h4 {
    color: white!important;
}
.h1-light {
    color: white!important;
}
div[style="background:#1976d2;color:white;padding:20px 25px;"] {
	background: #444!important;
}
a[style="color:#333;font-weight:500;"] {
	color: white!important;
}
}
