css:
@-moz-document domain(playing.se) {

.box {
    background-color:#232323;
}

.discussions-listing .data-listing {
    background-color:#232323;
}

.cta-container {
    background: #232323;
}

.discussions.discussions-listing>header {
    background: #232323;
}

.speech-bubble, blockquote, pre {
    background: #2f2f2f;
}

.new-reply-form textarea, .sceditor-container iframe {
    background: #151515;
}

.sceditor-toolbar {
    background: #232323;
}

blockquote::after {
    display: none;
}

html {
	background:#151515;   
}

.extra-content {
    background: #232323;
}

.discussions-listing .thread-visited {
	background: #232323;
}

.discussions-listing .discussion-title a {
    color: #d6d6d6;
}

.profile h1.username {
    color: #d6d6d6;
}

.data-listing a {
    color: #d6d6d6;
}

.table-header, th {
    color: #3fb2b2;
}

.block-link {
    color: #d6d6d6;
}

body {
	color: #d6d6d6;
}

.post-author-nick {
    color: #d6d6d6;
}

img.post-author-avatar.avatar[src*="avatar144dpi130x130"] {
    display: none;
}

.sceditor-container::after {
	display: none;
}

.data-listing>li>span, .table>li>span, td, th {
	border-bottom: 1px solid #333333;
}

.discussions.discussions-listing>header .view-switcher .current a {
	background: #232323;
}

.spoiler.show, .spoiler:active {
	color: #d6d6d6;
	background: 0;
}

.spoiler {
	color: #30d2dd;
	background: #30d2dd;
}
    
}
