css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("iqdb.org") {
  body {
    background-color: #1d1d1d;
    font-family: sans-serif;
  }

  body, td, th {
    color: #eee;
  }

  a {
    text-decoration: none;
  }

  a:link {
    color: #ccc;
  }

  a:visited {
    color: #aaa;
  }

  a:hover {
    color: #555;
  }

  .pages table {
    background-color: #2d2d2d;
    border: none;
    width: auto;
  }
}
