header {
    display: none !important;
}
.pure-u-custom-ad-skyscraper {
    display: none !important;
    opacity: 0 !important;
}
.pure-u-1 {
    /*display: none !important;
    opacity: 0 !important;*/ /*this works, however result of speedtest is not displayed*/
}
.high-placeholder {
    display: none !important;
    opacity: 0 !important;
}
.pure-u-custom-ad-rectangle {
    display: none !important;
    opacity: 0 !important;
}
.pure-u-1-2 {
    display: none !important;
    opacity: 0 !important;
}
.lower {
    display: none !important;
}
footer.site {
    display: none !important;
}

.main-content {
    padding: 0;
}
