    height: 100%;
    position: absolute;
    width: 100%;
}
.container {
    display: table;
    height: 100%;
    width: initial;
}
.pure-g {
    display: table-cell;
    vertical-align: middle;
}

.server-hosts-col {
    width: 100%;
}
