css:
@-moz-document domain(www.sweclockers.com) {

.adPanorama {
    padding: 10px 0;
    position: relative;
    min-height: 142px;
    zoom: 1;
    display: flex;
    justify-content: center;
    align-items: center;
    display: none;
}

.ad.adInsider {
    margin-bottom: 8px;
    display: none;
}

.header>.banner {
    min-height: 20px;
}

.adForumSticker {
    float: right;
    display: block;
    margin: 0 0 16px 10px;
    display: none;
}
.ad.adModule {
    margin-bottom: 28px;
    max-width: 480px;
    margin-left: auto;
    margin-right: auto;
    display: none;
}

.adOutsider {
    display: inline-block;
    zoom: 1;
    display: none;
}
}
