css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(google.com) {
    
/* AREA: 1. TOP */
    
    /* bold links on google apps up left */
    #gbar a {
        font-weight: bold !important;
    }

    /* Clean address and bold links */
    #guser {
        color:white !important;
        font-weight: bold !important;
    }
    
    /*sign out red*/ 
    a[class="e a7 ou"] {
        color:red !important;
        font-weight:bold !important;
    }  

/* END_AREA */

    
    
    
/* AREA 2. LEFT (NAVIGATION MENU) */

    /* Tasks bold and blue */
    #\:ps {
        color:blue !important ;
        font-weight:bold !important;
    }

    /* Contacts color green */
    #\:q5 {
        color:green !important ;
    }
    
    /* Compose mail color green */
    #\:r3 {
        color:green !important ;
    }
   
    /* Inbox link color blue */
    #\:r2 a {
        color:blue !important;
    }

    /* Starred link color orange */
    #\:r0 a {
         color:orange !important;
    }    

    /* Chats link color blue */
    #\:qy a {
        color: blue !important;
    }

    /* Sent Mail link color green*/
    #\:qw a {
        color:green !important;
    }

    /* Drafts link color lime */
    #\:qu a{
        color:lime !important ;
    }

    /* All mail link color black */
    #\:qs a{
        color:black !important ;
    }

    /* Spam link color yellow */
    #\:qq a{
        color:#FC0 !important ;
    }

    /* Trash link color red */
    #\:qo a{
        color:red !important ;
    }

/* END_AREA */





/* AREA 3. LEFT-RIGHT NAVIGATION */

    /* Bold blue on titles of gadgets: Chat, Labels, QuickContacts, etc */
    .pw {
        font-weight: bold !important;
        color: blue !important;    
    }

/* END_AREA */


    
    
    
/* AREA 4. BOTTOM */

    /* Remove bottom ad */
    .mn {
        display: none !important;
    }

/* END_AREA */

}
