/*******************************************************************

MathematicaStackexchange_Default-Customization v1.0 (27.08.2016)

*********************************************************************/


@-moz-document domain("mathematica.stackexchange.com") {

/*=========================*/
/*=== Output Blockquote ===*/
/*=========================*/

blockquote {
    padding: 0px 7px 2px 5px; /* keeps default total width and reduces top & bottom padding */
    background-color: #EFF0F1; /* same color as code bocks */
    border-left: 10px solid #FFF; /* uses left border in background-color to indent quoted output */ 
}


/*===============================================================*/
/*=== Editor Adjustment -> Correct Line Break for Code Blocks ===*/
/*===============================================================*/

.wmd-input {
    padding: 2px;
    width: 702px;
    font-size: 13px;
}
.wmd-button-bar {
    width: 702px;
}
.wmd-container .grippie {
    width: 700px;
}
.mdhelp {
	width: 702px !important;
}

#scroller {
    margin-left: 15px;
}
#sidebar, .sidebar {
    margin: 0px -15px 10px 15px;
}


}
