css:
@-moz-document domain("www.drupal.org") {
body {
  font-family: 'Open Sans', Helvetica, Arial, sans-serif;
  font-size: 0.875em;
}

body.node-type-project-issue .node-project-issue,
.block-project-issue,
.field-name-body {
  font-size: 1em;
  line-height: 1.75;
}

.node-type-guide #page .panel-layout .container-inner, .node-type-documentation #page .panel-layout .container-inner,
.container-12 {
  max-width: 1600px;
  padding: 0 1.5em;
}

@media (min-width: 40em) {
  body {
	  font-size: 0.9375em;
  }
}

@media (min-width: 47.5em) {
  body {
	  font-size: 0.9375em;
  }

	.container-12 {
  	padding: 0 2.5em;
	}
}

@media (min-width: 66em) {
  body {
    font-size: 1.125em;
  }
}

textarea {
  font-size: inherit;
}
}
