css:
@-moz-document url-prefix("https://userstyles.org/login") {
body{background:silver;color:black;text-shadow:0 0 10px white;}
#left-sidebar>* {background:silver;box-shadow:0 5px 1px red;}
#main-header{margin-left:auto;margin-right:auto;background:silver;border-radius:50px;width:280px;padding:20px 10px;box-shadow:0 5px 1px red,0 10px 10px white;}
a{color:black;text-decoration:none}
a:hover{text-shadow:0 2px 5px white}
a:visited{color:black;}
h1{color:black;text-shadow:0 2px 5px red,0 2px 10px white;}
#login{background:rgba(255,255,255,0.5);border-radius:50px;padding:40px 10px;box-shadow:0 0 30px black inset;}
.btn-auth{border-radius:50px;box-shadow:0 0 10px black;padding :10px;}
#login-existing{box-shadow:0 0 10px black;padding :8px 10px;outline:none;border-radius:50px;}
#password-existing{box-shadow:0 0 10px black;padding :8px 10px;outline:none;border-radius:50px;}

}
