css:
/* Name          : Supremacy 1914 Website Modification
 * Author        : Bergjnl
 * Version       : 0.1.1
 * Latest update : 2016-12-29
 * Desc          :
 *   Modification to the main website of supremacy,
 *   Makes it look better and more usable.
 *   Fixes some layout errors in the message window.
 */

@-moz-document
	domain("supremacy1914.com"),
	domain("supremacy1914.de"),
	domain("supremacy1914.nl"),
	domain("supremacy1914.es"),
	domain("supremacy1914.pl"),
	domain("supremacy1914.it"),
	domain("supremacy1914.fr"),
	domain("supremacy1914.gr")
	{

	.sg_bg {
		background: url("http://www.supremacy1914.com/fileadmin/templates/supremacy_1914/landing/7/lp7_back.jpg") no-repeat center top fixed;
	}

	#sg_main {
		-webkit-box-shadow: 0 0 20px 10px rgba(0,0,0,0.5);
		-moz-box-shadow: 0 0 20px 10px rgba(0,0,0,0.5);
		box-shadow: 0 0 20px 10px rgba(0,0,0,0.5);
		background: black;
	}

	/*
	 * Begin game overview
	 */

	#sg_betaUserPart
	{
		display: none;
	}

	/*
	 * End game overview
	 */

	.sg_premium_account_header_image
	{
		display: none;
	}

	td.sg_pi1-newpost-left input
	{
		width: 500px;
	}

	td.sg_pi1-newpost-right textarea
	{
		width: 100%;
	}

	/*
	 * Begin responsive website
	 */

	@media screen and (max-width: 960px) {
		html, body
		{
			max-width: 100%;
			overflow-x: hidden;
		}

		div.sg_align_c
		{
			margin-left: 0;
			margin-right: 0;
			text-align: left;
		}
	}

	/*
	 * End responsive website
	 */
}
