@-moz-document url-prefix("https://soundcloud.com") {
.content,
html,
body,
.commentForm__wrapper,
.commentForm__transition,
.l-container.l-content,
.uploadMain__chooserContainer,
.uploadMain__foot {
    background-color: hsl(0, 0%, 15%);
    background-image: none;
}
span,
p,
.sc-text,
.compactTrackListItem__number,
.compactTrackListItem__trackTitle,
.collectionSection__heading,
.g-type-centered,
.g-type-marketing-centered,
.emptyNetworkPage__headline,
.uploadMain__chooserContainer {
    color: hsl(0, 25%, 100%);
}
.gritter-item p {
    color: black;
}
a {
    color: hsl(20, 100%, 50%)!important;
}
a:hover {
    color: hsl(20, 100%, 75%)!important;
}
.sc-border-light-right {
    border-right: 1px solid hsla(0, 0%, 35%, 1)
}
.sc-border-light-left {
    border-left: 1px solid hsla(0, 0%, 35%, 1)
}
.sc-border-light-bottom,
.collection.m-overview .collection__section:not(:last-child) {
    border-bottom: 1px solid hsla(0, 0%, 35%, 1)
}
.sc-border-light-top {
    border-top: 1px solid hsla(0, 0%, 35%, 1)
}
.sc-border-light {
    border: 1px solid hsla(0, 0%, 35%, 1)
}
.g-tabs {
    border-bottom: 1px solid hsla(0, 0%, 35%, 1)
}
.sc-tag,
.sc-tag:visited {
    background: hsla(0, 0%, 25%, 1);
    border: none;
}
.commentForm__wrapper {
    border: 1px solid hsla(0, 0%, 25%, 1)
}
.commentForm.m-small .commentForm__input {
    background-color: hsl(0, 0%, 35%)
}
.sound__soundActions {
    background-color: hsla(0, 0%, 15%, 1)
}
.commentPopover.darkText .commentPopover__body {
    color: hsl(0, 25%, 100%)!important;
}
.l-fluid-fixed .l-sidebar-right .l-footer {
    background-color: hsl(0, 0%, 15%);
}
.sc-visuallyhidden span {
    color: hsl(0, 0%, 50%)!important;
}
.l-footer {
    background-color: hsl(0, 0%, 15%);
}
.l-listen-wrapper .l-about-rows {
    border-right: 1px solid hsla(0, 0%, 35%, 1)
}
.listenEngagement {
    border: none;
    box-shadow: 0 1px hsla(0, 0%, 35%, 1)
}
.playControls {
    -webkit-filter: invert(0.9);
    filter: invert(0.9);
    color: black!important;
}
.playbackSoundBadge__title > span {
    color: black!important;
}
.playbackTimeline__timePassed span,.playbackTimeline__duration span {
    color: black!important;
}
a.sc-link-light {
    color: hsl(200, 100%, 50%)!important;
}
.playControls g-z-index-header m-visible a {
    color: hsl(200, 100%, 50%)!important;
}
.playbackTimeline__progressBar {
    background-color: hsl(200, 100%, 50%)!important;
}
.truncatedAudioInfo.m-overflow.m-collapsed .truncatedAudioInfo__wrapper::after {
    content: ' ';
    position: absolute;
    left: 0;
    bottom: 0;
    height: 20px;
    width: 100%;
    background: #fff;
    background: linear-gradient(rgba(255, 73, 26, 0), rgba(255, 73, 26, .5)90%, #ff491a)
}
}
