css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("juick.com") {
	
	html {
	background:#EEEEE5;
	color:#222
}
#headdiv {
clear:both;
margin:0 0 5px;
padding:0 20px;
background:#EEEEE5;
border-top:1px solid #EEEEE5;
border-bottom:1px solid #EEEEE5;
position:relative
}
#newmessage {
background:#fff;
padding:15px;
margin-bottom:10px
}
	/* ТЕГИ */	
	article .tags {
	margin-top:1px
}
.msg-tags {
margin-top:5px
}
	.msg-tags>a,article .tags>a {
	font-size:11pt;
	display:inline-block;
	margin-bottom:1px;
	margin-right:1px;
	padding:0 3px;
	border:1px solid #ccc;
	color:#222
}
	/* Фон поста */
article {
margin-bottom:10px;
padding:10px;
line-height:130%;
background:#FFF
}
  	/* Комментарии */
	.msg-cont {
	background:#fff;
	margin-bottom:10px;
	padding:10px;
	width:640px;
	line-height:130%
}
.msg {
margin:10px 0 10px
}
}
