css:
@-moz-document url-prefix("http://candies.aniwey.net/") {
html, body {
	background: #101010 !important;
	color: #BBB !important;
}

a:link, a:visited {
	color: #808080;
}

table, th, td {
	border: 1px solid #BBB;
	padding: 3px;
}

button {
	border: 1px solid #404040;
	background: #202020;
	color: #BBB;
	
	margin-bottom: 2px;
	padding: 2px;
}

button:hover {
	border: 1px solid #404040;
	background: #303030;
	
	margin-bottom: 2px;
	padding: 2px;
}

button:disabled {
	border: 1px solid #606060;
	background: #404040;
	
	margin-bottom: 2px;
	padding: 2px;
}

input, select {
	border: 2px solid #404040;
	background: #303030;
	color: #BBB;
}

#tabs li button.active {
	background-color: #404040;
	color: #BBB;
}

#tabs li button {
	color: #BBB;
}

.toggle {
	background-color: #202020;
}
}
