css:
@-moz-document domain(www.creepypastafromthecrypt.com) {
body {
    background-image: url('https://i.gyazo.com/e45f54e2cc5516e2210c34453db5ab6e.jpg');
    background-size: cover;
}
img[src*="http://img.xooimage.com/files55/5/2/2/banni-re-44774da.jpg"] {
    width: 0;
    height: 243px;
    padding-right: 450px;
    background: url('https://i.gyazo.com/cb6296fcfd4f42b1b5fc2386f48391d4.png') top left no-repeat;
}
span[style*="color: #ff0000"] {
    color: #ffc500 !important;
}
td[class*="row"] {
    background-color: rgba(230, 230, 230, 1) !important;
    transition: 0.2s;
}
a[title="Menu"]:hover {
    transition: 0.2s;
}
a[href="javascript:void(0)"]:hover {
    font-size: 1.2em;
    transition: 0.2s;
}
a[href*="/profile.php?u="]:hover {
    font-size: 1.2em;
    transition: 0.2s;
}
td[style*="infobackground"] {
    background-color: rgba(140, 255, 140, 0.2) !important;
}
td div {
    background-image: none !important;
    background-color: rgba(225, 225, 225, 1) !important;
}
th {
    background-image: url('https://i.gyazo.com/e45f54e2cc5516e2210c34453db5ab6e.jpg');
    color: rgba(0, 0, 0, 0.7);
}
td.catLeft {
    background-image: url('https://i.gyazo.com/e45f54e2cc5516e2210c34453db5ab6e.jpg') !important;
}
td.catHead {
    background-image: url('https://i.gyazo.com/e45f54e2cc5516e2210c34453db5ab6e.jpg') !important;
}
td.rowpic {
    background-image: url('https://i.gyazo.com/e45f54e2cc5516e2210c34453db5ab6e.jpg') !important;
}
strong {
    color: rgba(0, 0, 0, 0.6);
    text-shadow: 0px 0px 1px rgba(0, 0, 0, 0.1);
}
div {
    color: rgba(0, 0, 0, 0.6);
}
tr.bg1 td.row1 {
    background-color: rgba(10, 10, 10, 0.1) !important;
}
tr.bg1 td.row2 {
    background-color: rgba(10, 10, 10, 0.1) !important;
}
tr.bg1 td.row1 div {
    background-color: rgba(50, 50, 50, 1) !important;
}
tr.bg1 td.row2 div {
    background-color: rgba(70, 70, 70, 1) !important;
}
a.postlink span {
    color: rgba(20, 20, 20, 0.5) !important;
}
#chatConnectedDiv {
    background-color: rgba(230, 230, 230, 1) !important;
    text-shadow: 1px 1px rgba(0, 0, 0, 0.6);
}
table.forumline {
    border: none;
}
span.postbody span{
    color: rgba(0,0,0,0.7);
}
span.postbody {
    color: rgba(0,0,0,0.7);
}
}
