css:
@-moz-document url-prefix("https://rocketmod.net/"), url-prefix("http://rocketmod.net/") {
#site-header, .site-header, #site-menu, #site-menu > li > a {
    background-color: #444;
    color: white;
}
#wrap-body, #wrap-subhead-main {
    background-color: #555;
    color: white;
}
#navigation #active-subsection a, #subhead-tabs li:first-child a {
    background-color: #555;
}
#navigation a:hover, #subhead-tabs a:hover, .dropdown-nonscroll > li > a:hover {
    background-color: #515151;
}
.dropdown .dropdown-contents {
    background-color: #666;
}
.side-block-body, ul.topiclist li.row dl, .stat-block > p, .panel, .stat-block > div, .bg2, #navigation, #navigation a, #subhead-tabs a {
    background-color: #444;
    color: white;
}
#online-list-stat {
    background-color: #454545;
    color: white;
}
#wrap-subhead {
    background-color: #494949;
    color: white;
}
a, a.topictitle, a.arrow-up, a.arrow-left, a.forumtitle, a.lastsuject, a.username, #site-search input, #site-search button, dt label, dd label, .panel h3, dt span, label, #wrap-subhead h2, #wrap-subhead h2 a, #breadcrumbs a, .search-box .inputbox, .search-box .button, .postbody .content, .postprofile {
    color: white !important;
}
.logo_h__txt, .logo_link {
    font: bold 40px/48px Michroma;
    color: #5acecd !important;
}
option {
    color: black
}
a:hover {
    color: #ccc;
}
body {
    background-color: #222;
}
.content h2, .panel h2 {
    color: #55ffff;
}

*::-webkit-scrollbar {
    width: 10px ! important;
    height: 10px ! important;
    background: #1e1e1e ! important;
    border: 1px solid #252525 ! important;
}
*::-webkit-scrollbar-button {
display: none ! important;
}
*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track {
border: 0 ! important;
box-shadow: none ! important;
}
*::-webkit-scrollbar-thumb {
    min-height: 28px ! important;
    background: #444 ! important;
}
*::-webkit-scrollbar-corner,
*::-webkit-scrollbar-track {
background: #1e1e1e ! important;
}
}
