/*
Author: 8cr14mov
Purpose: Darken and clean vodlocker frontend.
*/

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("vodlocker.com") {
  body, .content, .content-1col-nobox {
    background-color: #17181A;
    color: #797979;
    margin-top: 3%;
  }
  .content_bg {
    background: none;
  }
  div.footer, div.navbar, div.header, .corner-content-1col-bottom, .corner-content-1col-top, .videodetail, div#layout, .nav-primary, .disqus-footer__wrapper, #no-posts, #lightsout_shade, div.lightsout_shade:nth-child(8), #closeads_mute {
    display: none !important;
  }
  #btn_download {
    background: #252525;
    color: #797979;
  }
}
