/*
Author: 8cr14mov
Purpose: Darken and clean userstyles.org
*/

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("userstyles.org") {
  body, #main-header, #left-sidebar > * {
    background-color: #17181A;
    color: #797979;
  }
  td.date-value, tr, dt, dd, h1, h2, p, #Head a {
    color: #797979;
  }
  a {
    color: #7B4895;
    text-decoration: none;
  }
  a:hover {
    color: #975EB3;
    text-decoration: none;
  }
  .install, #stylish-installed-style-not-installed {
    background-color: #252525;
    color: #797979;
    background-image: none;
  }
  #screenshots {
    border: 1px #353535 solid;
  }
  .more {
    border-top: 1px #353535 solid;
  }
  pre {
    background: #17181A;
  }
  .Item.Read {
    background: #252525;
  }
  .Button, #Head, input.InputBox {
    background: #252525 !important;
    text-decoration: none !important;
    color: #797979 !important;
    text-shadow: none;
    box-shadow: none;
  }
  th, td {
    border: none !important;
  }
}
