/*
Author: 8cr14mov
Purpose: Darken, flatten, and clean promptfile interface.
*/

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("promptfile.com") {

  #footer, #header_wrap, #confirmbox h1, #confirmbox p, #confirmbox p.small {
    visibility: hidden !important;
  }
  body, #view_header, #confirmbox, .gray_btn {
    background-color: #17181A !important;
    color: #717171 !important;
    text-align: center !important;
  }
  .view_dl_link, .view_dl_link:hover {
    color: #797979 !important;
  }
  #not_found_msg {
    display: none !important;
  }
  #player_overlay #continue_btn {
    border: none !important;
  }
}
