css:
@-moz-document domain("expertplay.net") {
body {color: #74bbec;}
a:hover {color: #00CED1;}
#ipsLayout_header {background: #fbfbfb;}
.ipsAreaBackground_reset {
    background: #ffffff;}
.ipsDataItem {box-shadow: 0 -1px 0 #b2b3b9;}
.ipsBox:not( .ipsBox_transparent ):not( .ipsModerated ) {
    background-color: #e0e0e0;}
.ipsDataList {background: #ffffff;}
a {
    color: #507eda;}
.ipsType_sectionHead{color: #000;}
.ipsApp .ipsButton_important{border: 1px solid #25ccfb;
    box-shadow: 0px 0px 4px 0px #25fb8c;
    color: #0a6267;}
#secondaryHeader{background-color: #3a3d47;
    box-shadow: 0px 1px 10px 0px rgba(0,0,0,0.7);}
.ipsNavBar_primary > ul > li > a{color: #000000;}
.ipsNavBar_secondary > li > a{color: #000000;}
.ipsNavBar_secondary > li.ipsNavBar_active a {color: #000000;}
.ipsType_pageTitle{color: #000000;}
.ipsType_normal {font-size: 16px;color: #4a4747;}
.ipsBox:not( .ipsBox_transparent ):not( .ipsModerated ){background-color: #f3f2f2;}
.cUserHovercard {background-color: #f3f2f2;}
.ipsQuote {background: #e2e2e2;border-color: #bdbdbd;}
.ipsQuote_citation {background: #447a94;}
.ipsMenu {
    background: #efefef;border: 1px solid #4158a5;}
.ipsMenu_item > a, .ipsMenu_item > span {
    background: #ffffff;}
.ipsMenu_title {
    background: #eaeaea;}
.ipsApp .ipsButton_light {color: #fdfdfd;background-color: #2e323c;}
.ipsMenu_headerBar, .ipsMenu_footerBar {background-image: -webkit-linear-gradient( 0deg, #116098 0%, rgb(49, 67, 125) 100%);}
.ipsDataList_readStatus .ipsDataItem:not(.ipsDataItem_unread):not(.ipsDataItem_selected):not(.ipsModerated) {background: #ffffff;}
.ipsNavBar_primary > ul > li.ipsNavBar_active > a {color: #28b8b9;
    box-shadow: 0px 3px 0px #2581fb;}
.ipsNavBar_primary > ul > li > a {color: #000;}
.ipsApp .ipsButton_primary {    border: 2px solid #1fc192;
    background-color: #2e323c;}
.ipsBreadcrumb > ul:last-child > li:last-child{color: #060606;}
#elSearch {border: 2px solid #1ae4e4;
    box-shadow: 0px 0px 4px 0px #15cae0;}
ipsDataItem_title {font-size: 15px;}
.ipsBreadcrumb.ipsBreadcrumb_bottom {background-color: #ffffff;}
.ipsModal {background: rgba(255, 255, 255, 0.4);}
.ipsDataItem_title {font-size: 14px;}
.ipsWidget .ipsDataItem_title {font-size: 14px;}
}
