css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.redflagdeals.com") {
  #header_leaderboard, .forums_nav_sidebar_column, .sidebar_content, .first_post_ad
  {
    display: none;
  }
  
  .with_sidebar .primary_content
  {
    padding-right: 0.8rem;
  }

  .forums_nav_primary_column
  {
    padding-right: 0.5rem;
  }
  
  .forums_page_header
  {
    padding-top: 0.5rem !important;
    padding-bottom: 0.5rem !important;
  }
  
  nav#site_breadcrumbs {
    height: 30px !important;
  }
  
  .forums_page_header_buttons, .site_pagination_forums, .category_filter, .forums_page_header
  {
    margin: 0 !important;
  }
  
  .forum_group, .below_postlist, .search_results_header
  {
    margin-bottom: 20px;
  }
  
  .row_unread
  {
    padding: 0.35rem 0;
  }
  
  .search_results_header > h1
  {
    margin-bottom: 0;
    padding-bottom: 0.2rem;
  }
  .searchbox {
    background-color: black;
  }
  .topictitle_retailer
  {
    background-color: transparent;
  }
}
