css:
@-moz-document domain("cleverbot.com") {
html, body, div, body.pagetype-home.page-other.cbavatarasr.cbnotsignedin {
	background: #101010;
	color: #BBB;
	font-family: "Segoe UI";
}

#content {
	font-size: 100%;
	width: 600px;
}

#conversationcontainer {
	width: 600px;
}

form {
	width: 600px;
}

span.user {
	color: #BBB;
	font-size: 11pt;
	font-weight: normal;
}

span.bot {
	color: #BBB;
	font-size: 11pt;
	font-weight: bold;
}

input.stimulus {
	background-color: #202020 !important;
	color: #BBB !important;
	padding: 3px !important;
	
	padding-left: 0px;
	padding-right: 0px;
	border: none !important;
	
	width: 600px !important;
	height: inherit !important;
}

input.sayitbutton, input.thinkaboutitbutton, input.thinkformebutton {
	background-color: #202020 !important;
	color: #BBB !important;
	font-weight: bold !important;
	
	width: 150px !important;
}

/* EYE CANDY */
#cleverbotlogo {
	-webkit-filter: invert(93.75%);
}

#snipTextIcon {
	-webkit-filter: grayscale(100%) brightness(75%);
}

#cookieconsent {
	background-image: none;
	background-color: #182E48;
	color: #ACE;
}

#cbsocial, #footer {
	display: none;
}

#cookieconsent>a {
	background-color: #ACE;
	color: #182E48;
}
}
