css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://shop.lego.com/fr-FR/Pick-a-Brick") {

  .pab-item-summary div {
    padding: 0 5px 0 0;
  }
  
  .pab-item-summary__media img {
    width: 70px;
    height: 70px;
  }
  
  .pab-bag-item__media {
    padding: 0;
  }
  .pab-bag-item__media img {
    width: 70px;
    height: 70px;
  }
  .pab-bag-item__info {
    padding: 0 5px 0 0;
  }
  
  .pab-bag-item__btn-remove {
    bottom: 0vw;
    right: 0.5vw;
  }
  
}
